namespace Maanfee.Dashboard.Services.Controllers.Authentications
{
    [Route("api/[controller]")]
    [ApiController]
    [ApiExplorerSettings(IgnoreApi = true)]
    public class AuthenticationsController : _BaseController
    {
        public AuthenticationsController(UserManager<ApplicationUser> userManager
            , SignInManager<ApplicationUser> signInManager
            , RoleManager<IdentityRole> rolesManager
            , _BaseContext_SQLServer context
            , CommonService CommonService
            , HttpClient http
            , IHubContext<LoggingHub> loggingHub) : base(context, CommonService, http, loggingHub)
        {
            this.UserManager = userManager;
            this.SignInManager = signInManager;
            this.RolesManager = rolesManager;
        }

        private readonly UserManager<ApplicationUser> UserManager;
        private readonly SignInManager<ApplicationUser> SignInManager;
        private readonly RoleManager<IdentityRole> RolesManager;

        [HttpPost("Login")]
        public async Task<IActionResult> Login(LoginViewModel request)
        {
            var user = await UserManager.FindByNameAsync(request.UserName);
            if (user == null)
            {
                return BadRequest(DashboardResource.MessageItemAlreadyTaken);
            }
            var SingInResult = await SignInManager.CheckPasswordSignInAsync(user, request.Password, false);
            if (!SingInResult.Succeeded)
            {
                return BadRequest(DashboardResource.MessageInvalidUsername);
            }

            bool CanSignin = await SignInManager.CanSignInAsync(user);
            if (!CanSignin)
            {
                return BadRequest(DashboardResource.MessageUserIsNotExsist);
            }

            var CustomClaims = new[]
            {
                new Claim("Id", user.Id),
                new Claim("Name", user.Name),
                new Claim("UserName", user.UserName),
            };

            //await SignInManager.SignInAsync(user, request.RememberMe, CustomClaims);
            await SignInManager.SignInWithClaimsAsync(user, request.RememberMe, CustomClaims);
            //await SignInManager.PasswordSignInAsync(user, request.Password, request.RememberMe, lockoutOnFailure: false);

            if (LoggingHub is not null)
            {
                await LoggingHub.Clients.All.SendAsync("ReceiveMessage", new LogInfo
                {
                    Platform = LoggingPlatformDefaultValue.Server,
                    LogDate = DateTime.Now,
                    Message = $"{user.UserName} ({user.Name}) - is logged in",
                    Level = LogLevel.Information,
                });
            }

            return Ok();
        }

        [HttpPost("Register")]
        public async Task<IActionResult> Register(RegisterViewModel RegisterViewModel)
        {
            var GUID = Guid.NewGuid().ToString().Substring(0, 7).Replace("-", new Random().Next(0, 9).ToString());

            #region - User -

            var user = new ApplicationUser();
            user.UserName = RegisterViewModel.UserName;
            user.Password = RegisterViewModel.Password;
            user.Email = string.Empty;
            user.Avatar = Convert.FromBase64String("");
            user.PersonalCode = GUID;
            //user.Phrase = RegisterViewModel.Phrase.Encrypt();

            // defaults
            //FirstName = string.Empty,
            //LastName = string.Empty,
            //Name = string.Empty,

            #endregion

            var result = await UserManager.CreateAsync(user, RegisterViewModel.Password);
            if (!result.Succeeded)
            {
                return BadRequest(result.Errors.FirstOrDefault()?.Description);
            }

            #region - Add Role -

            if (result.Succeeded)
            {
                bool IsRoleExists = await RolesManager.RoleExistsAsync(UserRoleDefaultValue.User);
                if (IsRoleExists)
                {
                    var Role = await RolesManager.FindByNameAsync(UserRoleDefaultValue.User);
                    if (Role != null)
                    {
                        await UserManager.AddToRoleAsync(user, Role.Name);
                    }
                }
                else
                {
                    await RolesManager.CreateAsync(new IdentityRole(UserRoleDefaultValue.User));
                }
            }

            #endregion

            return await Login(new LoginViewModel
            {
                UserName = RegisterViewModel.UserName,
                Password = RegisterViewModel.Password
            });
        }

        [HttpPost("ResetPassword")]
        public async Task<IActionResult> ResetPassword(ResetPasswordViewModel ResetPasswordViewModel)
        {
            var user = await db_SQLServer.ApplicationUsers.FirstOrDefaultAsync(x => x.Password == ResetPasswordViewModel.Password);
            if (user == null)
            {
                return BadRequest("Invalid phrase");
            }

            var currentpassword = user.Password;
            var result = await UserManager.ChangePasswordAsync(user, currentpassword, ResetPasswordViewModel.Password);
            if (!result.Succeeded)
            {
                return BadRequest(result.Errors.FirstOrDefault()?.Description);
            }

            try
            {
                user.Password = ResetPasswordViewModel.Password;
                await db_SQLServer.SaveChangesAsync();
            }
            catch
            {
                return BadRequest("Error 666");
            }

            return await Login(new LoginViewModel
            {
                UserName = user.UserName,
                Password = ResetPasswordViewModel.Password,
            });
        }

        [Authorize]
        [HttpPost("Logout")]
        public async Task<IActionResult> Logout()
        {
            await SignInManager.SignOutAsync();
            return Ok();
        }

        [HttpGet("CurrentUserInfo")]
        public CurrentUser CurrentUserInfo()
        {
            return new CurrentUser
            {
                IsAuthenticated = User.Identity.IsAuthenticated,
                UserName = User.Identity.Name,
                Claims = User.Claims.ToDictionary(c => c.Type, c => c.Value)
            };
        }

